mapper.select:
INSERT INTO user_chat (receiver, receiverName, sender, senderName, message, sendTime, owner, type)
VALUES (#{receiver}, #{receiverName}, #{sender}, #{senderName}, #{message}, #{sendTime}, #{owner},
        #{type});



SELECT uc.sender, uc.senderName, uc.receiverName, uc.receiver, uc.sendTime, uc.type, uc.owner, uc.message
FROM (
SELECT sender, senderName, receiverName, receiver, sendTime, type, owner, message,
ROW_NUMBER() OVER (PARTITION BY sender ORDER BY sendTime DESC) AS n
FROM user_chat
WHERE receiver = #{key}
) AS uc WHERE uc.n > 21 ;
